import React, { useEffect, useState, useCallback } from 'react';
import { getData } from '../actions/index';
import { useSelector, useDispatch } from 'react-redux';
import { Table } from 'antd';

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dataReady, setDataReady] = useState(false);
  const st = useSelector((state) => state.data);
  const dispatch = useDispatch();

  useEffect(() => {
    // loading(true);
    async function asyncFunc() {
      await dispatch(getData());
    }
    asyncFunc();
  }, [dispatch]);

  const columns = [
    {
      title: 'Date',
      dataIndex: 'fullDate',
      key: 'fullDate',
    },
    {
      title: 'Day of week',
      dataIndex: 'day',
      key: 'day',
    },
    {
      title: 'Weight',
      dataIndex: 'weight',
      key: 'weight',
    },
    {
      title: 'Gap from yesterday',
      dataIndex: 'gap_from_yesterday',
      key: 'yesterday_gap',
    },
    {
      title: 'Exercise',
      dataIndex: 'exercise',
      key: 'exercise',
    },
    {
      title: 'Gap_from_beginning',
      dataIndex: 'gap_from_beginning',
      key: 'beginning_gap',
    },
    {
      title: 'comment',
      dataIndex: 'comment',
      key: 'comment',
    },
  ];

  let arr = [];

  useEffect(() => {
    if (st.data && st.data.length > 0) {
      st.data.map((data, index) => arr.push({ ...data, key: data.id }));
      setDataReady(true);
      console.log('arr ready');
    } else {
      return;
    }
  }, [st.data, arr]);

  return (
    console.log('arr is', arr),
    (
      <div style={{ margin: 30 }}>
        <div>
          <h1>Home</h1>
        </div>
        {dataReady && <Table key={Math.random()} columns={columns} dataSource={arr} />}
      </div>
    )
  );
};

export default Home;
