import { USER_AUTH } from './types';
import axios from 'axios';

export const signup = (data, reDirect) => async (dispatch) => {
  console.log('signup working on client');
  try {
    console.log('signup working in actions');
    const res = await axios.post('http://localhost:5000/users/signUp', data);
    const result = res.data;
    console.log('result is', result);
    reDirect('/signin');
  } catch (e) {
    console.log('problem with signing up on client side', e);
  }
};

export const signin = (data) => async (dispatch) => {
  console.log('sign in working');
  try {
    const res = await axios.post('http://localhost:5000/users/signIn', data);
    dispatch({ type: USER_AUTH, payload: res.data });
    reDirect('/home');
  } catch (e) {
    console.log('problem with signing in', e);
  }
};
