import React, { useState, useEffect } from 'react';
import { Menu } from 'antd';
import { NavLink } from 'react-router-dom';

const Nav = () => {
  const [selected, setSelected] = useState('');
  const [location, setLocation] = useState(window.location.pathname);
  const loc = window.location.pathname;
  // const location = loc === '/add-check' ? 'Add check' : '/signin' ? 'Signin' : 'Home';
  console.log('location is', location);

  useEffect(() => {
    setSelected(location);
  }, [location]);

  const handleClick = (e) => {
    console.log('e is', e);
    console.log('e.current is', e.key);
    setLocation(e.key);
  };

  const showNav = () => {
    return (
      <Menu onClick={handleClick} mode='horizontal'>
        <Menu.Item key={location != 'signup' ? 'signin' : 'signup'}>
          <NavLink to='/signin'>Signin</NavLink>
        </Menu.Item>
        <Menu.Item key='Home'>
          <NavLink to='/'>Home</NavLink>
        </Menu.Item>
        <Menu.Item key='Add check'>
          <NavLink to='/add-check'>Add check</NavLink>
        </Menu.Item>
      </Menu>
    );
  };

  return console.log('location is', location), (<div>{showNav()}</div>);
};

export default Nav;
