import React, { useState } from 'react';

import { addData } from '../actions/actions';

import { Form, Input, Button, Checkbox } from 'antd';
import { Radio } from 'antd';

const MainForm = () => {
  const [weight, setWeight] = useState('');
  const [exercise, setExercise] = useState(false);

  const layout = {
    // labelCol: { span: 8 },
    wrapperCol: { span: 16 },
  };
  const tailLayout = {
    // wrapperCol: { offset: 8, span: 16 },
  };
  const onFinish = (values) => {
    console.log('Success:', values);
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };
  const createForm = () => {
    return (
      <Form
        {...layout}
        name='basic'
        // initialValues={{ remember: true }}
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
      >
        <Form.Item
          label='Weight'
          name='name'
          rules={[{ required: true, message: 'Please input your username!' }]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label='Comment'
          name='comment'
          rules={[{ required: true, message: 'Please input your password!' }]}
        >
          <Input.TextArea />
        </Form.Item>
        <Form.Item name='radio-button' label='Did you exercise?' style={{ marginBottom: 20 }}>
          <Radio.Group>
            <Radio required value={true} onChange={() => setExercise(true)}>
              Yes
            </Radio>
            <Radio value={false} onChange={() => setExercise(false)}>
              No
            </Radio>
          </Radio.Group>
        </Form.Item>
        <Form.Item {...tailLayout}>
          <Button type='primary' htmlType='submit'>
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  };
  return (
    console.log('exercise is', exercise),
    (
      <div>
        <div style={{ margin: 20 }}>{createForm()}</div>
      </div>
    )
  );
};

export default MainForm;

