import axios from 'axios';
import { FETCH_DATA } from './types';

export const addData = (data) => async (dispatch) => {
  console.log('adding data');
  try {
    await axios.post('http://localhost:5000/create/add_weight', data);
  } catch (e) {
    console.log(e.response);
    e.response && console.log('problem with adding weight', e.response);
  }
};

export const getData = () => async (dispatch) => {
  try {
    const res = await axios.get('http://localhost:5000/fetch/get_data');
    dispatch({ type: FETCH_DATA, payload: res.data });
  } catch (e) {
    console.log(e.response);
    e.response && console.log('problem with getting data', e.response);
  }
};
