import axios from 'axios';
import { FETCH_DATA, MAIN_ERROR } from './types';
// import { ADD_DATA } from './types';

export const addData = (data, reDirect) => async (dispatch) => {
  console.log('redirect', reDirect);
  try {
    const res = await axios.post('http://localhost:5000/create/add_weight', data);
    console.log(res.data);
    reDirect('/home');
  } catch (e) {
    e.response && console.log(e.response.data);
  }
};

export const getData = (email) => async (dispatch) => {
  try {
    const res = await axios.post('http://localhost:5000/fetch/get_data', { email });
    console.log('final data is', res.data);
    dispatch({ type: FETCH_DATA, payload: res.data });
  } catch (e) {
    e.response ? dispatch({ type: MAIN_ERROR, payload: e.response.data }) : console.log('problem');
  }
};
