import React, { useEffect, useState } from 'react';
import { getData } from '../actions/index';
import { useSelector, useDispatch } from 'react-redux';
import { Table, Tag } from 'antd';

const Home = () => {
  const [data, setData] = useState([]);
  const st = useSelector((state) => state.data);
  const dispatch = useDispatch();

  useEffect(() => {
    async function asyncFunc() {
      await dispatch(getData());
    }
    asyncFunc();
  }, [dispatch]);

  const columns = [
    {
      title: 'Date',
      dataIndex: 'fullDate',
    },
    {
      title: 'DOW',
      dataIndex: 'day',
    },
    {
      title: 'Weight',
      dataIndex: 'weight',
    },
    {
      title: 'Gap from yesterday',
      dataIndex: 'gap_from_yesterday',
    },
    {
      title: 'Exercise',
      dataIndex: 'exercise',
    },
    {
      title: 'Gap_from_beginning',
      dataIndex: 'gap_from_beginning',
    },
  ];

  const tableData2 = [
    {
      id: 1,
      fullDate: 'Thu Apr 16 2020',
      day: 'Thursday',
      weight: 84.1,
      gap_from_yesterday: 999,
      exercise: 0,
      gap_from_beginning: 1.8,
      comment: 'no workout',
    },
    {
      id: 1,
      fullDate: 'Thu Apr 16 2020',
      day: 'Thursday',
      weight: 84.1,
      gap_from_yesterday: 999,
      exercise: 0,
      gap_from_beginning: 1.8,
      comment: 'no workout',
    },
  ];
  let arr = [];

  const a = data.map((dat) => {
    arr.push(dat.fullDate, dat.day, dat.weight);
  });

  a();

  return (
    console.log('data is', data.length > 0),
    (
      <div style={{ margin: 30 }}>
        <div>
          <h1>Home</h1>
        </div>
        {/* {data.length > 0 &&  <Table columns={columns} dataSource={someData()} />} */}
      </div>
    )
  );
};

export default Home;
