import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addData } from '../actions/index';
import { Form, Input, Button } from 'antd';
import { Radio } from 'antd';
import './comp.css';

const MainForm = (props) => {
  console.log('props are', props);

  const dispatch = useDispatch();
  // eslint-disable-next-line
  const [exercise, setExercise] = useState(false);
  const onFinish = (values) => {
    dispatch(addData(values));
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };
  const createForm = () => {
    return (
      <Form name='basic' onFinish={onFinish} onFinishFailed={onFinishFailed}>
        <Form.Item
          label='Weight'
          name='weight'
          rules={[{ required: true, message: 'Please input your username!' }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label='Comment'
          name='comment'
          // rules={[{ required: true, message: 'Please input your password!' }]}
        >
          <Input.TextArea />
        </Form.Item>
        <Form.Item
          // rules={[{ required: true, message: "We're on to you, say it !" }]}
          name='exercise'
          label='Did you exercise?'
          style={{ marginBottom: 20 }}
          colon={false}
        >
          <Radio.Group>
            <Radio value={true} onChange={() => setExercise(true)}>
              Yes
            </Radio>
            <Radio value={false} onChange={() => setExercise(false)}>
              No
            </Radio>
          </Radio.Group>
        </Form.Item>
        <Form.Item>
          <Button type='primary' htmlType='submit' size='large' style={{ borderRadius: '10%' }}>
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  };
  return (
    <div id='abc'>
      <div>
        <h1>Enter your data</h1>
      </div>
      <div>{createForm()}</div>
    </div>
  );
};

export default MainForm;
