import { USER_ADDED, SIGNIN } from './types';
import axios from 'axios';

export const signup = (data, reDirect) => async (dispatch) => {
  console.log('signup working on server');
  try {
    console.log('signup working in actions');
    const res = await axios.post('http://localhost:5000/users/signUp', data);
    const result = res.data;
    dispatch({ type: USER_ADDED });
    reDirect('/home');
  } catch (e) {
    console.log('problem with signing up on client side', e);
  }
};

export const signin = (dispatch) => {
  console.log('signup working in actions');
  dispatch({ type: SIGNIN });
};
