import React, { useEffect, useState } from 'react';
import { getData } from '../actions/index';
import { useSelector, useDispatch } from 'react-redux';
import { Table, Tag } from 'antd';

const Home = () => {
  const [data, setData] = useState([]);
  const st = useSelector((state) => state.data);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getData());
  }, [dispatch]);

  useEffect(() => {
    setData(st.data);
  }, [st]);

  const columns = [
    {
      title: 'Date',
      dataIndex: 'fullDate',
    },
    {
      title: 'DOW',
      dataIndex: 'day',
    },
    {
      title: 'Weight',
      dataIndex: 'weight',
    },
    {
      title: 'Gap from yesterday',
      dataIndex: 'gap_from_yesterday',
    },
    {
      title: 'Exercise',
      dataIndex: 'exercise',
    },
    {
      title: 'Gap_from_beginning',
      dataIndex: 'gap_from_beginning',
    },
  ];

  const tableData2 = [
    {
      id: 1,
      fullDate: 'Thu Apr 16 2020',
      day: 'Thursday',
      weight: 84.1,
      gap_from_yesterday: 999,
      exercise: 0,
      gap_from_beginning: 1.8,
      comment: 'no workout',
    },
    {
      id: 1,
      fullDate: 'Thu Apr 16 2020',
      day: 'Thursday',
      weight: 84.1,
      gap_from_yesterday: 999,
      exercise: 0,
      gap_from_beginning: 1.8,
      comment: 'no workout',
    },
  ];

  return (
    console.log('dats is', data),
    (
      <div style={{ margin: 30 }}>
        <div>
          <h1>Home</h1>
        </div>
        <Table columns={columns} dataSource={tableData2} />{' '}
      </div>
    )
  );
};

export default Home;
