import React from 'react';
import { Menu } from 'antd';
import { NavLink } from 'react-router-dom';

const Nav = () => {
  const [selected, setSelected] = useState('');

  const handleClick = (e) => {
    setSelected(e.current);
  };

  const showNav = () => {
    return (
      <Menu onClick={handleClick} mode='horizontal' selectedKeys={selected}>
        <Menu.Item key='mail'>
          <NavLink to='/'>Home</NavLink>
        </Menu.Item>
        <Menu.Item key='app'>
          <NavLink to='/add-check'>Add check</NavLink>
        </Menu.Item>
      </Menu>
    );
  };
  return <div>{showNav()}</div>;
};

export default Nav;
