import axios from 'axios';
import { FETCH_DATA, MAIN_ERROR } from './types';
import { ADD_DATA } from './types';

export const addData = (data, history) => async (dispatch) => {
  try {
    const res = await axios.post('http://localhost:5000/create/add_weight', data);
    console.log(res.data);
    history.push('/');
  } catch (e) {
    e.response && console.log(e.response.data);
    console.log('problem is', e);
  }
};

export const getData = () => async (dispatch) => {
  try {
    const res = await axios.get('http://localhost:5000/fetch/get_data');
    dispatch({ type: FETCH_DATA, payload: res.data });
  } catch (e) {
    console.log(e.response.data);
    dispatch({ type: MAIN_ERROR, payload: e.response.data });
  }
};
