import React, { useState } from 'react';
import { addData } from '../actions/actions';
import { Form, Input, Button } from 'antd';
import { Radio } from 'antd';
import './comp.css';

const MainForm = () => {
  const [exercise, setExercise] = useState(false);

  const layout = {
    wrapperCol: { span: 16 },
  };

  const onFinish = (values) => {
    const { weight, comment, exercise } = values;
    addData(values);
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };
  const createForm = () => {
    return (
      <Form {...layout} name='basic' onFinish={onFinish} onFinishFailed={onFinishFailed}>
        <Form.Item
          label='Weight'
          name='weight'
          rules={[{ required: true, message: 'Please input your username!' }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label='Comment'
          name='comment'
          // rules={[{ required: true, message: 'Please input your password!' }]}
        >
          <Input.TextArea />
        </Form.Item>
        <Form.Item
          // rules={[{ required: true, message: "We're on to you, say it !" }]}
          name='exercise'
          label='Did you exercise?'
          style={{ marginBottom: 20 }}
          colon={false}
        >
          <Radio.Group>
            <Radio value={true} onChange={() => setExercise(true)}>
              Yes
            </Radio>
            <Radio value={false} onChange={() => setExercise(false)}>
              No
            </Radio>
          </Radio.Group>
        </Form.Item>
        <Form.Item>
          <Button type='primary' htmlType='submit' size='large' style={{ borderRadius: '10%' }}>
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  };
  return (
    console.log('exercise is', exercise),
    (
      <div id='abc'>
        <div>
          <h1>Enter your data</h1>
        </div>
        <div>{createForm()}</div>
      </div>
    )
  );
};

export default MainForm;
