import { MAIN_ERROR } from '../actions/types';

const errorsReducer =
  (state,
  (action) => {
    switch (action.type) {
      case MAIN_ERROR:
        return {
          ...state,
          errors: action.payload,
        };
    }
  });

export default errorsReducer;
