import React, { useEffect, useState } from 'react';
import { getData } from '../actions/index';
import { useSelector, useDispatch } from 'react-redux';
import { Table, Spin } from 'antd';
import './comp.css';

const Home = () => {
  const [loading, setLoading] = useState(false);
  // const [errors, setErrors] = useState(false);
  const st = useSelector((state) => state.data);
  const errorsFromRedux = useSelector((state) => state.errors.error);
  const dispatch = useDispatch();

  console.log('errors are:', errorsFromRedux);

  useEffect(() => {
    setLoading(true);
    async function asyncFunc() {
      await dispatch(getData());
      setLoading(false);
    }
    asyncFunc();
    errorsFromRedux && setErrors(errorsFromRedux);
  }, [dispatch]);

  const columns = [
    {
      title: 'Date',
      dataIndex: 'fullDate',
      key: 'fullDate',
    },
    {
      title: 'Day of week',
      dataIndex: 'day',
      key: 'day',
    },
    {
      title: 'Weight',
      dataIndex: 'weight',
      key: 'weight',
    },
    {
      title: 'Gap from yesterday',
      dataIndex: 'gap_from_yesterday',
      key: 'yesterday_gap',
    },
    {
      title: 'Exercise',
      dataIndex: 'exercise',
      key: 'exercise',
    },
    {
      title: 'Gap_from_beginning',
      dataIndex: 'gap_from_beginning',
      key: 'beginning_gap',
    },
    {
      title: 'comment',
      dataIndex: 'comment',
      key: 'comment',
    },
  ];

  return (
    <div style={{ margin: 30 }}>
      {loading ? (
        <Spin size='large' className='spinner' />
      ) : errorsFromRedux ? (
        <h1>{errorsFromRedux}</h1>
      ) : (
        <div>
          {st.data && st.data.length > 0 && (
            <Table
              rowClassName={(record, index) => (record.gap_from_yesterday < 0 ? 'lost' : 'gained')}
              bordered
              scroll
              pagination={false}
              rowKey={(record) => Math.random()}
              columns={columns}
              dataSource={st.data}
            />
          )}
        </div>
      )}
    </div>
  );
};

export default Home;
