import React, { useState, useEffect } from 'react';
import { Menu } from 'antd';
import { NavLink } from 'react-router-dom';

const Nav = () => {
  const [selected, setSelected] = useState('');
  const loc = window.location.pathname;
  const location = loc === '/add-check' ? 'Add check' : '/signin' ? 'Signin' : 'Home';

  useEffect(() => {
    setSelected(location);
  }, [location]);

  const handleClick = (e) => {
    console.log('e is', e);
    console.log('e.current is', e.key);
    setSelected(e.key);
  };

  const showNav = () => {
    return (
      <div>
        <Menu onClick={handleClick} mode='horizontal' selectedKeys={selected}>
          <Menu.Item key='Signin'>
            <NavLink to='/signin'>Signin</NavLink>
          </Menu.Item>
          <Menu.Item key='Home'>
            <NavLink to='/'>Home</NavLink>
          </Menu.Item>
          <Menu.Item key='Add check'>
            <NavLink to='/add-check'>Add check</NavLink>
          </Menu.Item>
        </Menu>
        <h4>Dont have an account ? register here</h4>
      </div>
    );
  };

  return console.log('selected is', selected), (<div>{showNav()}</div>);
};

export default Nav;
